import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import InputItem from './inputitems';
import { HiArrowRight } from 'react-icons/hi';
import { AiOutlineMail } from 'react-icons/ai';
import { BsTelephone } from 'react-icons/bs';
import { GiAges } from 'react-icons/gi';
import { FaBirthdayCake } from 'react-icons/fa';
import { MdOutlineSchool } from 'react-icons/md';
import { CgWebsite } from 'react-icons/cg';
import 'animate.css';
import Resume from './resume';

const PromptUser = () => {
    const navigate = useNavigate();
    const [userObject, setUserObject] = useState({});
    const [imageSource, setImageSource] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUserObject(prevState => ({ ...prevState, [name]: value }));
    };

    const onButtonClick = () => {
        alert(`WINDOWS DIMENSION : ${window.innerWidth}, ${window.innerHeight}`);
        navigate('/resume', { state: { userObject, imageSource } });
    };

    return (
        <div className='prompt-container animate_animated animate_bounce'>
            <div className='inputs'>
                <InputItem className='firstName' onChange={handleChange} label='First Name' placeholder='Enter your first name' name='firstName' />
                <InputItem className='lastName' onChange={handleChange} label='Last Name' placeholder='Enter your last name' name='lastName' />
                <InputItem className='email' onChange={handleChange} label={<AiOutlineMail />} placeholder='Enter your email' name='email' />
                <InputItem className='phone' onChange={handleChange} label={<BsTelephone />} placeholder='Enter phone number' name='phone' />
                <InputItem className='website' onChange={handleChange} label={<CgWebsite />} placeholder='Enter your website' name='website' />
                <InputItem className='birth-date' onChange={handleChange} label={<FaBirthdayCake />} type='date' name='DOB' />
                <InputItem className='age' onChange={handleChange} label={<GiAges />} type='number' name='age' placeholder='Enter your age' />
                <InputItem className='skills' onChange={handleChange} label='Skill' name='skills' placeholder='List your skills' />
                <InputItem className='experience' onChange={handleChange} label={<MdOutlineSchool />} name='experience' placeholder='Enter your experience' />
                <InputItem className='gradYear' onChange={handleChange} label='Graduation Year' name='gradYear' placeholder='Enter your Graduation Year' />
                <InputItem className='certs' onChange={handleChange} label='Certifications' placeholder='Enter your Certifications' name='certs' />
                <InputItem className='uni' onChange={handleChange} label='University' placeholder='Enter University Name' name='uni' />
                <InputItem className='interests' onChange={handleChange} label='Interests' placeholder='Enter your Interests' name='interests' />

                <button className='generate-button' onClick={onButtonClick}>
                    Generate Resume <HiArrowRight />
                </button>
            </div>
        </div>
    );
};

export default PromptUser;
