import React from 'react';
import InputItem from './inputitems';

const Step2 = ({ userObject, handleChange, handleExperienceChange, addExperience }) => {
    return (
        <div className='step'>
            <h2>Work Experience</h2>
            {userObject.experience && userObject.experience.map((exp, index) => (
                <div key={index} className='experience-group'>
                    <InputItem className='title' onChange={(e) => handleExperienceChange(index, e)} label='Title' placeholder='Enter your job title' name='title' value={exp.title} />
                    <InputItem className='company' onChange={(e) => handleExperienceChange(index, e)} label='Company' placeholder='Enter company name' name='company' value={exp.company} />
                    <InputItem className='dates' onChange={(e) => handleExperienceChange(index, e)} label='Dates' placeholder='Enter employment dates' name='dates' value={exp.dates} />
                    <InputItem className='location' onChange={(e) => handleExperienceChange(index, e)} label='Location' placeholder='Enter location' name='location' value={exp.location} />
                    {exp.description.map((desc, descIndex) => (
                        <InputItem key={descIndex} className='description' onChange={(e) => handleExperienceChange(index, e, descIndex)} label={`Description ${descIndex + 1}`} placeholder='Enter job description' name='description' value={desc} />
                    ))}
                    <button type='button' onClick={() => handleExperienceChange(index, 'addDescription')}>Add Description</button>
                </div>
            ))}
            <button type='button' onClick={addExperience}>Add Experience</button>
        </div>
    );
};

export default Step2;
