import React from 'react';
import styled from 'styled-components';

// Styled components
const Row = styled.div`
  display: flex;
  flex-direction: column;
  margin-bottom: 1rem;
`;

const InputWrapper = styled.div`
  position: relative;
  display: flex;
  flex-direction: column;
`;

const StyledInput = styled.input`
  border: 1px solid #ccc;
  padding: 0.5rem;
  font-size: 1rem;
  border-radius: 4px;
  transition: all 0.3s ease;

  &:focus {
    border-color: #007bff;
    outline: none;
  }
`;

const StyledTextArea = styled.textarea`
  border: 1px solid #ccc;
  padding: 0.5rem;
  font-size: 1rem;
  border-radius: 4px;
  transition: all 0.3s ease;
  resize: vertical;

  &:focus {
    border-color: #007bff;
    outline: none;
  }
`;

const Label = styled.label`
  margin-bottom: 0.5rem;
  font-weight: bold;
  color: #333;
`;

const InputItem = ({ label, placeholder = ' ', name, onChange, type = 'text', isTextArea = false, className }) => {
  return (
    <Row className={className}>
      <InputWrapper>
        <Label>{label}</Label>
        {!isTextArea ? (
          <StyledInput onChange={onChange} placeholder={placeholder} name={name} type={type} />
        ) : (
          <StyledTextArea onChange={onChange} placeholder={placeholder} name={name} />
        )}
      </InputWrapper>
    </Row>
  );
};

export default InputItem;
