import React from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import InputItem from './inputitems';

const Step2 = ({ userObject, handleChange, handleExperienceChange, addExperience }) => {
    const handleDateChange = (index, date, name) => {
        const newExperience = [...userObject.experience];
        newExperience[index] = { ...newExperience[index], [name]: date };
        handleExperienceChange(newExperience);
    };

    const handleCheckboxChange = (index, e) => {
        const newExperience = [...userObject.experience];
        newExperience[index] = { ...newExperience[index], currentlyWorking: e.target.checked };
        if (e.target.checked) {
            newExperience[index].endDate = null; // Clear the end date if currently working
        }
        handleExperienceChange(newExperience);
    };

    return (
        <div className='step'>
            <h2>Work Experience</h2>
            {userObject.experience && userObject.experience.map((exp, index) => (
                <div key={index} className='experience-group'>
                    <InputItem 
                        className='title' 
                        onChange={(e) => handleExperienceChange(index, e)} 
                        label='Title' 
                        placeholder='Enter your job title' 
                        name='title' 
                        value={exp.title} 
                    />
                    <InputItem 
                        className='company' 
                        onChange={(e) => handleExperienceChange(index, e)} 
                        label='Company' 
                        placeholder='Enter company name' 
                        name='company' 
                        value={exp.company} 
                    />

                    <label htmlFor={`startDate-${index}`}>Start Date</label>
                    <DatePicker
                        selected={exp.startDate}
                        onChange={(date) => handleDateChange(index, date, 'startDate')}
                        dateFormat="MM/yyyy"
                        showMonthYearPicker
                        placeholderText='Select start date'
                        name={`startDate-${index}`}
                    />

                    <label htmlFor={`endDate-${index}`}>End Date</label>
                    <DatePicker
                        selected={exp.endDate}
                        onChange={(date) => handleDateChange(index, date, 'endDate')}
                        dateFormat="MM/yyyy"
                        showMonthYearPicker
                        placeholderText='Select end date'
                        name={`endDate-${index}`}
                        disabled={exp.currentlyWorking}
                    />

                    <label htmlFor={`currentlyWorking-${index}`}>
                        <input
                            type='checkbox'
                            checked={exp.currentlyWorking}
                            onChange={(e) => handleCheckboxChange(index, e)}
                            id={`currentlyWorking-${index}`}
                        />
                        Currently Working
                    </label>

                    <InputItem 
                        className='location' 
                        onChange={(e) => handleExperienceChange(index, e)} 
                        label='Location' 
                        placeholder='Enter location' 
                        name='location' 
                        value={exp.location} 
                    />

                    {exp.description.map((desc, descIndex) => (
                        <InputItem 
                            key={descIndex} 
                            className='description' 
                            onChange={(e) => handleExperienceChange(index, e, descIndex)} 
                            label={`Description ${descIndex + 1}`} 
                            placeholder='Enter job description' 
                            name='description' 
                            value={desc} 
                        />
                    ))}

                    <button type='button' onClick={() => handleExperienceChange(index, 'addDescription')}>Add Description</button>
                </div>
            ))}
            <button type='button' onClick={addExperience}>Add Experience</button>
        </div>
    );
};

export default Step2;
