import React from 'react';
import styled from 'styled-components';

const Row = styled.div`
    margin-bottom: 20px;
    display: flex;
    flex-direction: column;
`;

const InputWrapper = styled.div`
    position: relative;
    margin-top: 10px;
`;

const InputField = styled.input`
    width: 100%;
    padding: 10px;
    border-radius: 4px;
    border: 1px solid #ccc;
    font-size: 16px;
    &:focus {
        outline: none;
        border-color: #007BFF;
    }
`;

const TextAreaField = styled.textarea`
    width: 100%;
    padding: 10px;
    border-radius: 4px;
    border: 1px solid #ccc;
    font-size: 16px;
    resize: vertical;
    &:focus {
        outline: none;
        border-color: #007BFF;
    }
`;

const Label = styled.label`
    position: absolute;
    top: -10px;
    left: 10px;
    background-color: white;
    padding: 0 5px;
    font-size: 14px;
    color: #666;
    transition: all 0.2s;
    ${InputField}:focus + &,
    ${TextAreaField}:focus + & {
        color: #007BFF;
    }
`;

const InputItem = ({ label, placeholder = ' ', name, onChange, type = 'text', isTextArea = false, className }) => {
    return (
        <Row className={className}>
            <InputWrapper>
                {!isTextArea ? (
                    <InputField onChange={onChange} placeholder={placeholder} name={name} type={type} />
                ) : (
                    <TextAreaField onChange={onChange} placeholder={placeholder} name={name}></TextAreaField>
                )}
                <Label>{label}</Label>
            </InputWrapper>
        </Row>
    );
};

export default InputItem;
